exports.checkList = function(cb) {
	console.log("checkList");
	if (dataBuffer.value == 0) { 
	suggestList(function(data) {
		dataBuffer = data;
		cb(dataBuffer);
	});
  } else {
		cb(dataBuffer);
		
  }
	
};


suggestList = function(cb) {
	// body...
	//console.log("function getSuggestList");
	db.view('Jobdata/by_suggesttest', {
	    group: true,
	}, function(err, res) {
	    if (err) throw err.error
			// console.dir(res);
			//suggestData = res;
			//console.dir(suggestData);
			if (_.isEmpty(res)) return cb({noData:true});
			console.log("data get");
			var dataRes = {};
				
			for (i in res) {
				dataRes[res[i].key]=res[i].value;
		  };
			//console.dir(dataRes);
			// res = {
			// 	
			// 	key: res[0].key,
			// 	value:data
			// };
			//console.dir(res);
			//console.log(this);
			
			return cb(dataRes);
	});	
};

exports.suggest = function(suggest,cb) {
	//var suggestData = {}; 
	console.time("jobdata CouchDB");
	db.view('Jobdata/by_suggest', {
	    startkey: suggest,
	    endkey: suggest+"ZZZZ",
	    group: true,
		
	}, function(err, res) {
	    if (err) throw err.error
			// console.dir(res);
			//suggestData = res;
			//console.dir(suggestData);
			if (_.isEmpty(res)) return cb({noData:true});
			var dataRes = {};
			//console.dir(res);
			for (i in res) {
				dataRes[res[i].key]=_.flatten(res[i].value);
		  }
			//console.dir(dataRes);
			// res = {
			// 	
			// 	key: res[0].key,
			// 	value:data
			// };
			//console.dir(res);
			console.timeEnd("jobdata CouchDB");
			return cb(dataRes);
			});
			

};