var cradle = require('cradle');
var connection = new(cradle.Connection)('http://192.168.3.110', 5984, {
    auth: {
        username: 'web',
        password: 'web'
    }
});

var db = connection.database('jobdata-development');

exports.suggest = function(suggest,cb) {
	//var suggestData = {}; 
	db.view('Jobdata/by_suggest', {
	    startkey: suggest,
	    endkey: suggest+"ZZZZ",
	    limit: 5,
	    group: true
	}, function(err, res) {
	    if (err) throw err.error
			// console.dir(res);
			//suggestData = res;
			//console.dir(suggestData);
			return cb(res);
			});
			

};